import JeecgOrderTicketModal from "./form/JeecgOrderTicketModal";
import { JeecgListMixin } from '@/mixins/JeecgListMixin';
import { getAction } from '@/api/manage';
export default {
  name: "JeecgOrderTicketList",
  mixins: [JeecgListMixin],
  components: {
    JeecgOrderTicketModal: JeecgOrderTicketModal
  },
  data: function data() {
    return {
      description: '机票信息',
      // 表头
      columns: [{
        title: '航班号',
        align: "center",
        dataIndex: 'ticketCode'
      }, {
        title: '航班时间',
        align: "center",
        dataIndex: 'tickectDate'
      }, {
        title: '订单号码',
        align: "center",
        dataIndex: 'orderId'
      }, {
        title: '创建人',
        align: "center",
        dataIndex: 'createBy'
      }, {
        title: '创建时间',
        align: "center",
        dataIndex: 'createTime',
        sorter: true
      }, {
        title: '操作',
        key: 'operation',
        align: "center",
        width: 130,
        scopedSlots: {
          customRender: 'action'
        }
      }],
      url: {
        list: "/test/order/listOrderTicketByMainId",
        delete: "/test/order/deleteTicket",
        deleteBatch: "/test/order/deleteBatchTicket"
      }
    };
  },
  methods: {
    loadData: function loadData(arg) {
      var _this = this;

      if (arg === 1) {
        this.ipagination.current = 1;
      }

      var params = this.getQueryParams(); //update-begin--Author:kangxiaolin  Date:20190905 for：[442]主子表分开维护，生成的代码子表的分页改为真实的分页--------------------

      getAction(this.url.list, {
        orderId: params.mainId,
        pageNo: this.ipagination.current,
        pageSize: this.ipagination.pageSize
      }).then(function (res) {
        if (res.success) {
          _this.dataSource = res.result.records;
          _this.ipagination.total = res.result.total;
        } else {
          _this.dataSource = null;
        }
      }); //update-end--Author:kangxiaolin  Date:20190905 for：[442]主子表分开维护，生成的代码子表的分页改为真实的分页--------------------
    },
    getOrderMain: function getOrderMain(orderId) {
      this.queryParam.mainId = orderId;
      this.loadData(1);
    },
    handleAdd: function handleAdd() {
      this.$refs.modalForm.add(this.queryParam.mainId);
      this.$refs.modalForm.title = "添加机票信息";
    }
  }
};
