    handleMultiSelectChange: function handleMultiSelectChange(value, id, row, column) {
      this.multiSelectValues = this.bindValuesChange(value, id, 'multiSelectValues'); // 做单个表单验证

      this.validateOneInput(value, row, column, this.notPassedIds, true, 'change'); // 触发valueChange 事件

      this.elemValueChange(FormTypes.list_multi, row, column, value);
    },
    handleSearchSelectChange: function handleSearchSelectChange(value, id, row, column) {
      this.searchSelectValues = this.bindValuesChange(value, id, 'searchSelectValues');
      this.validateOneInput(value, row, column, this.notPassedIds, true, 'change');
      this.elemValueChange(FormTypes.sel_search, row, column, value);
    },
    filterOption: function filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    }
  }
};
