import JSelectDepartModal from './modal/JSelectDepartModal'
export default {
  name: 'JSelectDepart',
  components:{
    JSelectDepartModal
  },
  props:{
    modalWidth:{
      type:Number,
      default:500,
      required:false
    },
    multi:{
      type:Boolean,
      default:false,
      required:false
    },
    rootOpened:{
      type:Boolean,
      default:true,
      required:false
    },
    value:{
      type:String,
      required:false
    },
    disabled:{
      type: Boolean,
      required: false,
      default: false
    },
    // 自定义返回字段，默认返回 id
    customReturnField: {
      type: String,
      default: 'id'
    }
  },
  data(){
    return {
      visible:false,
      confirmLoading:false,
      departNames:"",
      departIds:''
    }
  },
  mounted(){
    this.departIds = this.value
  },
  watch:{
    value(val){
      if (this.customReturnField === 'id') {
        this.departIds = val
      }
    }
  },
  methods:{
    initComp(departNames){
      this.departNames = departNames
    },
    openModal(){
      this.$refs.innerDepartSelectModal.show()
    },
    handleOK(rows, idstr) {
      let value = ''
      if (!rows && rows.length <= 0) {
        this.departNames = ''
        this.departIds = ''
      } else {
        value = rows.map(row => row[this.customReturnField]).join(',')
        this.departNames = rows.map(row => row['departName']).join(',')
        this.departIds = idstr
      }
      this.$emit("change", value)
    },
    getDepartNames(){
      return this.departNames
    },
    handleEmpty(){
      this.handleOK('')
    }
  },
  model: {
    prop: 'value',
    event: 'change'
  }
}
